package com.spark.control;import com.spark.utils.FileUtil;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.http.HttpHeaders;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import org.springframework.web.multipart.MultipartFile;import java.util.Arrays;@RequestMapping(value = "/utils")@RestControllerpublic class UtilControl {    private final Logger logger = LoggerFactory.getLogger(UtilControl.class);    @PostMapping(value = "/uploadjar")    public ResponseEntity uploadJar(@RequestParam("files") MultipartFile fileEntity){        logger.debug("Single file upload!");        if (fileEntity.isEmpty()) {            return new ResponseEntity("please select a file!", HttpStatus.NO_CONTENT);        }        boolean isOk = FileUtil.saveUploadedFiles(Arrays.asList(fileEntity));        if(isOk){            return new ResponseEntity("Successfully uploaded - " + fileEntity.getOriginalFilename(), new HttpHeaders(), HttpStatus.OK);        }else {            return new ResponseEntity(HttpStatus.BAD_REQUEST);        }    }}